import { EnvelopeIcon } from "@heroicons/react"

const stats = [
    { id: 1, icon: <EnvelopeIcon/>, name: 'contact@romain-gilot.fr', value: 'Email' },
    { id: 2, name: 'www.romain-gilot.fr', value: 'Site web' },
    { id: 3, name: 'Metz, France', value: 'Lieu' },
]

function Contact() {
    return(
        <>
            <div className="bg-white py-24 sm:py-32">
                <div className="mx-auto max-w-7xl px-6 lg:px-8">
                    <dl className="grid grid-cols-1 gap-x-8 gap-y-16 text-center lg:grid-cols-3">
                    {stats.map((stat) => (
                        <div key={stat.id} className="mx-auto flex max-w-xs flex-col gap-y-4">
                        <dt className="text-gray-600">{stat.icon} {stat.name}</dt>
                        <dd className="order-first text-2xl font-semibold tracking-tight text-gray-900 sm:text-2xl">
                            {stat.value}
                        </dd>
                        </div>
                    ))}
                    </dl>
                </div>
            </div>
        </>
    )
}
export default Contact;
